// 使用 larkcloud 调用轻服务功能
const inspirecloud = require('@byteinspire/inspirecloud-api');
const { roleTable } = require('../models/roleTable');
const { categoryTable } = require('./categoryTable');
const { str2number } = require('../utils/transform');
const ObjectId = inspirecloud.db.ObjectId;

// 使用轻服务 news 表
// 若用户未创建，在发送第一条调用时会自动创建该表
const newsTable = inspirecloud.db.table('news');

class News {
  _id;
  title; // string
  region; // string
  author; // string
  auditState; // number
  publishState; // number
  createTime; // number
  publishTime; // number
  view; // number
  star; // number
  roleId; // ObjectId
  categoryId; // ObjectId

  constructor(params) {
    const { _id, title, region, author, auditState, publishState, view, star, roleId, categoryId } = params;
    _id===undefined?this._id=null:this._id=ObjectId(_id);
    title===undefined?this.title=null:this.title=title;
    region===undefined?this.region=null:this.region=region;
    author===undefined?this.author=null:this.author=author;
    if (auditState===undefined) this.auditState=null;
    else {
      if (typeof(auditState)==='string') this.auditState=str2number(auditState);
      else this.auditState=auditState;
    }
    if (publishState===undefined) this.publishState=null;
    else {
      if (typeof(publishState)==='string') this.publishState=str2number(publishState);
      else this.publishState=publishState;
    }
    if (view===undefined) this.view=null;
    else {
      if (typeof(view)==='string') this.view=str2number(view);
      else this.view=view;
    }
    if (star===undefined) this.star=null;
    else {
      if (typeof(star)==='string') this.star=str2number(star);
      else this.star=star;
    }
    roleId===undefined?this.roleId=null:this.roleId=ObjectId(roleId);
    categoryId===undefined?this.categoryId=null:this.categoryId=ObjectId(categoryId);
  }
}

